#!/bin/bash

if [[ $#--lt-2-| -lt 2 ]];
	then echo &quot;add the uname as first arg and ssh key as second&quot;
	exit
fi

USERNAME=$1
KEY=$2

# make new user
sudo useradd -m $USERNAME

USERPATH=/home/$USERNAME

echo &quot;Successfully created new user $USERNAME&quot;
echo &quot;&quot;
echo &quot;$USERNAME&#039;s ssh key:&quot;

sudo mkdir $USERPATH/.ssh

# set up user&#039;s ssh keys
sudo echo &quot;$KEY&quot; | sudo tee -a $USERPATH/.ssh/authorized_keys

sudo chown $USERNAME $USERPATH/.ssh
sudo chown $USERNAME $USERPATH/.ssh/authorized_keys

sudo chmod 700 $USERPATH/.ssh
sudo chmod 600 $USERPATH/.ssh/authorized_keys

# if the group hf-users exists, add them
if [ $(getent group hf-users) ]; then
	echo &quot;adding $USERNAME to hf-users...&quot;
	sudo usermod -aG hf-users $USERNAME
fi

# give the user a temporary password
if [[ $#--lt-3-| -lt 3 ]];
	then tmp_passwd=&quot;$(cat /dev/urandom | tr -dc &#039;a-zA-Z0-9&#039; | fold -w 10 | head -n 1)&quot;
else
	tmp_passwd=$3
fi
echo -e &quot;$tmp_passwd\n$tmp_passwd&quot; | (sudo passwd $USERNAME) &amp;&gt; /dev/null
echo &quot;&quot;
echo &quot;Setting password...&quot;
echo &quot;TEMPORARY PASSWORD FOR $USERNAME : $tmp_passwd&quot;
#sudo passwd -e $USERNAME &amp;&gt; /dev/null
echo &quot;$USERNAME will be prompted to change password on next login.&quot;
